package com.calculoimc.calculo;import java.util.HashMap;import java.util.Map;public class CalculoImc {    public Map<String, String> calcularImc(Double peso, Double altura, String nome) {        Double imc = peso / (altura * altura);        imc = Math.ceil(imc);        Map<String, String> dados = new HashMap();        dados.put("nome", nome);        dados.put("imc", imc.toString());        return dados;    }}