package com.calculoimc.resource;import com.calculoimc.calculo.CalculoImc;import javax.ws.rs.GET;import javax.ws.rs.Path;import javax.ws.rs.Produces;import javax.ws.rs.QueryParam;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("imc")public class ImcResource {    @GET    @Produces(MediaType.APPLICATION_JSON)    public Response calculaImc(            @QueryParam("nome") String nome,            @QueryParam("peso") Double peso,            @QueryParam("altura") Double altura) {        CalculoImc calculoImc = new CalculoImc();        return Response.ok(calculoImc.calcularImc(peso, altura, nome)).build();    }}//Url://http://localhost:8080/api/imc?nome=Vanderson&peso=72&altura=1.76